mapper.select:
select seq_notice, email, subject, content, writedate, status from ( select rownum as RNO, seq_notice, email, subject, content, writedate, status from ( select seq_notice, email, subject , case when length(content) > 10 then substr(content , 1, 10) || '....' else content end as content , to_char(writedate,'yyyy-mm-dd') as writedate , status from tbl_notice where status = 1
and ${colname} like '%'|| #{search} ||'%'
order by seq_notice desc ) V ) T where T.RNO >= #{start} and T.RNO <= #{end}

select * from ( select rownum as RNO, seq_question, email, subject, content, writedate, status, replystatus from ( select seq_question, email, subject, content, to_char(writedate,'yyyy-mm-dd') as writedate, status, replystatus from tbl_question )v where status = 1
and ${colname} like '%'|| #{search} ||'%'
order by seq_question desc ) T where T.RNO >= #{start} and T.RNO <= #{end}

select seq_question, email, subject, content, writedate, status
from tbl_answer
where seq_question = #{seq_question}
